import React, { useState, useEffect }  from "react";
import { Link, useNavigate } from "react-router-dom";
import Btnmd from './btn/Btnmd';
import axios from 'axios';
import Validation from "./Validation";

function Connect() {
 // INITIALISATION DES VALEURS
  const [values, setValues] = useState({
    mailUser: '',
    passwordUser: ''
  })

  const navigate = useNavigate;

axios.defaults.withCredentials = true;

const [errors, setErrors] = useState({})

const handleInput = (event) => {
  setValues(prev=> ({...prev,[event.target.name]: [event.target.value]}))
}

// ACTION DU CLIQUE SUR LE BOUTON VALIDER INSCRIPTION
const handleSubmit = (event) => {
  event.preventDefault();
  setErrors(Validation(values));
  if(errors.mailUser === "" && errors.passwordUser === "" ){
      axios.post('http://localhost:3001/', 
      {pseudoUser: pseudoReg, mailUser: mailReg,passwordUser: passwordReg})
      .then(res => {
        if(res.data === "Success"){
          navigate('/myprofile')
        }else{
          alert("L'inscription a échouée");
        }
      })
      .catch(err => console.log(err));
  }
}

// useEffect(() => {
//   axios.get('http://localhost:3001/')
//   .then(res => console.log(res));
// }, []);

    return (
        <section className="loginPage">
        <h1 className="titrePresentation mt-3">Bienvenue sur Together, <br/> le nouveau réseau social gratuit.</h1>
        <div className="blocForm">
        <form onSubmit={handleSubmit} className="formConnect rounded" method="POST" action="#">
            <p className="dejaInscrit">Déjà inscrit ?</p>
            <div className="m-4">
              <label htmlFor="exampleInput" className="form-label">Adresse Mail</label>
              <input onChange={handleInput} type="mail" className="form-control" id="mail" name="mailUser" autoComplete="off"/>
              {errors.mailUser && <span className="text-danger">{errors.mailUser}</span>}
            </div>
            <div className="m-4">
              <label htmlFor="exampleInput" className="form-label">Mot de passe</label>
              <input onChange={handleInput} type="password" className="form-control" id="passw" name="passwordUser" autoComplete="off"/>
              {errors.passwordUser && <span className="text-danger">{errors.passwordUser}</span>}
            </div>
            <p className="text-danger m-4">Mot de passe oublié ?</p>
            <Btnmd  type="submit" className="btn" caracteristique="md" text="Se connecter"/>
          </form>
        </div>
          <div className="noAccount">
            <p>Pas de compte ? <Link to="/register" className="inscrivezVous">Inscrivez-vous !</Link></p>
          </div>
        </section>
    );
  }

  export default Connect;
